media_attachments.description:
INSTALL sqlite;
INSTALL postgres;
LOAD sqlite;
LOAD postgres;

-- Attach both databases
ATTACH 'posts_processed.db' AS sqlite_db (TYPE sqlite);
ATTACH 'postgresql://u:p@ip:5432/db_name' AS pg_db (TYPE postgres);

INSERT INTO pg_db.processed_posts
SELECT * FROM sqlite_db.processed_posts s
WHERE NOT EXISTS (
  SELECT 1 FROM pg_db.processed_posts p
  WHERE p.post_id = s.post_id
);
